sourcesContent:
import Happi from 'happi';

const start  = async () =>{

    const server = new Happi.server({
        port : 8000,
        host : 'localhost',
    });

    server.route({
        method : 'GET',
        path : '/hello',
        handler : (request, h) => {
            return 'Hello World';
        }
    });

    await server.start();
    console.log(`server running at ${server.info.uri}`);

}

process.on('unhandledRejection', (err) => {

    console.log(err);
    process.exit(1);
});

start();

